import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent implements OnInit{

  //Usar dentro do HTML usando <p>{{evento.Tema}}</p>
  public eventos: any = [{
      Tema: 'Angular',
      Local: 'Belo Horizonte'
    },
    {
      Tema: 'Java',
      Local: 'São Paulo'
    },
    {
      Tema: 'Phyton',
      Local: 'Tanabi'
    }
]
  constructor(){}
  ngOnInit():void{

  }
}
<h1>Eventos do Components</h1>

<!-- Chamando o Evento.tema do OnInit da clase eventos.component.ts -->

<!-- Uma especie de foreach que faz a leitura de todos os eventos dentro do objeto em eventos.componet.ts -->
<div *ngFor="let evento of eventos">
  <p> <b>Tema:</b> {{evento.Tema}}</p>
  <p> <b>Local:</b> {{evento.Local}}</p>
  <hr>
</div>
